import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Home from './components/Home';
import Credits from './components/Credits';
import Debits from './components/Debits';

class App extends Component {
  constructor() {
    super();
    this.state = {
      accountBalance: 0,
      credits: [],
      debits: []
    };
  }

  componentDidMount() {
    fetch('https://johnnylaicode.github.io/api/credits.json')
      .then(response => response.json())
      .then(creditsData => {
        this.setState({ credits: creditsData });
        this.calculateBalance();
      });

    fetch('https://johnnylaicode.github.io/api/debits.json')
      .then(response => response.json())
      .then(debitsData => {
        this.setState({ debits: debitsData });
        this.calculateBalance();
      });
  }

  calculateBalance = () => {
    const totalCredits = this.state.credits.reduce((total, credit) => total + credit.amount, 0);
    const totalDebits = this.state.debits.reduce((total, debit) => total + debit.amount, 0);
    this.setState({ accountBalance: totalCredits - totalDebits });
  }

  addCredit = (description, amount) => {
    const newCredit = {
      id: Date.now(),
      description,
      amount: parseFloat(amount),
      date: new Date().toISOString().split('T')[0]
    };
    this.setState(prevState => ({
      credits: [...prevState.credits, newCredit]
    }), this.calculateBalance);
  }

  addDebit = (description, amount) => {
    const newDebit = {
      id: Date.now(),
      description,
      amount: parseFloat(amount),
      date: new Date().toISOString().split('T')[0]
    };
    this.setState(prevState => ({
      debits: [...prevState.debits, newDebit]
    }), this.calculateBalance);
  }

  render() {
    return (
      <Router basename="/Bank-of-React">
        <div>
          <Route exact path="/" render={() => <Home accountBalance={this.state.accountBalance} />} />
          <Route path="/credits" render={() => <Credits credits={this.state.credits} addCredit={this.addCredit} accountBalance={this.state.accountBalance} />} />
          <Route path="/debits" render={() => <Debits debits={this.state.debits} addDebit={this.addDebit} accountBalance={this.state.accountBalance} />} />
        </div>
      </Router>
    );
  }
}

export default App;
